package org.com.doctorservice.mapper;import lombok.experimental.UtilityClass;import org.com.doctorservice.dto.ScheduleOverrideRequest;import org.com.doctorservice.dto.ScheduleOverrideResponse;import org.com.doctorservice.dto.ScheduleTemplateResponse;import org.com.doctorservice.model.ScheduleOverride;import org.com.doctorservice.model.ScheduleTemplate;import java.util.List;@UtilityClasspublic class ScheduleMapper {    public static ScheduleTemplateResponse toDTO(ScheduleTemplate scheduleTemplate) {        ScheduleTemplateResponse scheduleTemplateResponse = ScheduleTemplateResponse.builder()                .id(scheduleTemplate.getId().toString())                .doctorId(scheduleTemplate.getDoctorId().toString())                .dayOfTheWeek(scheduleTemplate.getCustomDayOfTheWeek().toString())                .startTime(scheduleTemplate.getStartTime().toString())                .endTime(scheduleTemplate.getEndTime().toString())                .breakStartTime(scheduleTemplate.getBreakStartTime().toString())                .breakEndTime(scheduleTemplate.getBreakEndTime().toString())                .slotDurationOfMinutes(scheduleTemplate.getSlotDurationOfMinutes().toString())                .effectiveFrom(scheduleTemplate.getEffectiveFrom().toString())                .effectiveTo(scheduleTemplate.getEffectiveTo().toString())                .build();        return scheduleTemplateResponse;    }    public static ScheduleOverrideResponse toDTO(ScheduleOverride scheduleOverride) {        ScheduleOverrideResponse scheduleOverrideResponse = ScheduleOverrideResponse.builder()                .id(scheduleOverride.getId().toString())                .doctorId(scheduleOverride.getDoctorId().toString())                .date(scheduleOverride.getDate().toString())                .overrideType(scheduleOverride.getOverrideType().toString())                .startTime(scheduleOverride.getStartTime().toString())                .endTime(scheduleOverride.getEndTime().toString())                .slotDurationOfMinutes(scheduleOverride.getSlotDurationOfMinutes().toString())                .reason(scheduleOverride.getReason())                .build();        return scheduleOverrideResponse;    }}